GenreFact.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix t: <http://tutori.al/>
construct { _:x a t:GenreFact . _:x t:actor ?p .
            _:x t:label ?g . _:x t:value ?cnt }
{ select ?p ?g (count(?f) as ?cnt)
  { ?f :producer ?p. ?f :genre/:film_genre_name ?g }
  group by ?p ?g
}

filmBudgets.rdf:
prefix : <http://dbpedia.org/ontology/>
prefix t: <http://tutori.al/>
construct { ?x t:budget ?y }
{ ?x :budget ?y . ?x a :Film }

filmGross.rdf:
prefix : <http://dbpedia.org/ontology/>
prefix t: <http://tutori.al/>
construct { ?x t:gross ?y }
{ ?x :gross ?y . ?x a :Film }

produced-interlink.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix owl: <http://www.w3.org/2002/07/owl#>
prefix t: <http://tutori.al/>
construct { ?producer t:produced ?x . ?x a t:Film . ?x owl:sameAs ?film }
{ ?x :producer ?producer . ?x a :film . ?x owl:sameAs ?film }

numFilmsExecutiveProduced.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix t: <http://tutori.al/>
construct { ?p t:numFilmsExecutiveProduced ?cnt }
{  select ?p (count(?f) as ?cnt)
   { ?f2 :producer ?p . ?f :executive_producer ?p }
   group by ?p ?f2
}

numFilmsProduced.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix t: <http://tutori.al/>
construct { ?p t:numFilmsProduced ?cnt }
{ select ?p (count(?f) as ?cnt) 
  { ?f :producer ?p . ?p a :producer }
  group by ?p
}

names.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix t: <http://tutori.al/>
construct { ?p t:name ?n }
{ 
select distinct ?p ?n
  { ?f :producer ?p . ?p :producer_name ?n }
}

theProducers.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix t: <http://tutori.al/>
construct { ?p a t:Producer }
{ 
select distinct ?p 
  { ?f :producer ?p }
}

FilmCompanyCoop.rdf:
prefix : <http://data.linkedmdb.org/resource/movie/>
prefix t: <http://tutori.al/>
construct { ?f a t:FilmCompanyCoop . ?f t:actor ?p . 
            ?f t:withFilmCompany ?c }
{ select ?f ?p ?c
  { ?f a :film . ?f :producer ?p .
    ?f :production_company ?cp . 
    ?cp :production_company_name ?c }
}
